	(11, 2, '日志拦截器LogInterceptor', '<pre class="brush:java;toolbar:false">package&nbsp;com.ruixin.common.interceptor;\n\nimport&nbsp;javax.servlet.http.HttpServletRequest;\nimport&nbsp;javax.servlet.http.HttpServletResponse;\n\nimport&nbsp;com.ruixin.common.utils.StringUtils;\nimport&nbsp;com.ruixin.util.LogUtils;\nimport&nbsp;org.apache.shiro.authz.annotation.RequiresPermissions;\nimport&nbsp;org.springframework.web.method.HandlerMethod;\nimport&nbsp;org.springframework.web.servlet.HandlerInterceptor;\nimport&nbsp;org.springframework.web.servlet.ModelAndView;\n\nimport&nbsp;java.lang.reflect.Method;\n\n\n/**\n&nbsp;*&nbsp;日志拦截器\n&nbsp;*/\npublic&nbsp;class&nbsp;LogInterceptor&nbsp;implements&nbsp;HandlerInterceptor&nbsp;{\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;boolean&nbsp;preHandle(HttpServletRequest&nbsp;request,&nbsp;HttpServletResponse&nbsp;response,&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Object&nbsp;handler)&nbsp;throws&nbsp;Exception&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;void&nbsp;postHandle(HttpServletRequest&nbsp;request,&nbsp;HttpServletResponse&nbsp;response,&nbsp;Object&nbsp;handler,&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ModelAndView&nbsp;modelAndView)&nbsp;throws&nbsp;Exception&nbsp;{\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;public&nbsp;void&nbsp;afterCompletion(HttpServletRequest&nbsp;request,&nbsp;HttpServletResponse&nbsp;response,&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Object&nbsp;handler,&nbsp;Exception&nbsp;ex)&nbsp;throws&nbsp;Exception&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;保存日志\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;permission=null;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(handler&nbsp;instanceof&nbsp;HandlerMethod)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Method&nbsp;m&nbsp;=&nbsp;((HandlerMethod)&nbsp;handler).getMethod();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;RequiresPermissions&nbsp;rp&nbsp;=&nbsp;m.getAnnotation(RequiresPermissions.class);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;permission&nbsp;=&nbsp;(rp&nbsp;!=&nbsp;null&nbsp;?&nbsp;StringUtils.join(rp.value(),&nbsp;&quot;,&quot;)&nbsp;:&nbsp;null);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(permission!=null&amp;&amp;permission.startsWith(&quot;base&quot;))&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;LogUtils.saveLog(request,&nbsp;handler,&nbsp;ex,&nbsp;null);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;}\n\n}</pre><p><br/></p>', '2018-03-26 10:03:19', '1', '2018-04-28 21:03:24', '1', '0', '3'),
	(12, 2, 'springboot配置pageHelper', '<pre class="brush:java;toolbar:false">package&nbsp;com.ruixin.config;\n\nimport&nbsp;com.github.pagehelper.PageHelper;\nimport&nbsp;org.springframework.context.annotation.Bean;\nimport&nbsp;org.springframework.context.annotation.Configuration;\n\nimport&nbsp;java.util.Properties;\n\n/**\n&nbsp;*&nbsp;Created&nbsp;by&nbsp;ruixin&nbsp;on&nbsp;2017/12/20.\n&nbsp;*&nbsp;Description:注册MyBatis分页插件PageHelper\n&nbsp;*/\n@Configuration\npublic&nbsp;class&nbsp;MybatisConfiguration&nbsp;{\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;PageHelper&nbsp;pageHelper(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;PageHelper&nbsp;pageHelper=new&nbsp;PageHelper();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Properties&nbsp;properties=new&nbsp;Properties();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;properties.setProperty(&quot;offsetAsPageNum&quot;,&quot;true&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;properties.setProperty(&quot;rowBoundsWithCount&quot;,&quot;true&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;properties.setProperty(&quot;reasonable&quot;,&quot;true&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;pageHelper.setProperties(properties);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;pageHelper;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n}</pre><p><br/></p>', '2018-02-26 10:16:34', '1', '2018-05-04 20:58:58', '13', '0', '2'),
	(13, 2, 'springboot配置Shiro', '<pre class="brush:java;toolbar:false">package&nbsp;com.ruixin.config;\n\nimport&nbsp;java.util.HashMap;\nimport&nbsp;java.util.Map;\n\nimport&nbsp;javax.servlet.DispatcherType;\nimport&nbsp;javax.servlet.Filter;\n\nimport&nbsp;com.ruixin.common.security.URLPermissionsFilter;\nimport&nbsp;com.ruixin.common.security.UserRealm;\nimport&nbsp;net.sf.ehcache.CacheManager;\nimport&nbsp;net.sf.ehcache.config.CacheConfiguration;\nimport&nbsp;org.apache.shiro.authc.credential.HashedCredentialsMatcher;\nimport&nbsp;org.apache.shiro.cache.ehcache.EhCacheManager;\nimport&nbsp;org.apache.shiro.spring.LifecycleBeanPostProcessor;\nimport&nbsp;org.apache.shiro.spring.web.ShiroFilterFactoryBean;\nimport&nbsp;org.apache.shiro.web.filter.authc.AnonymousFilter;\nimport&nbsp;org.apache.shiro.web.mgt.DefaultWebSecurityManager;\nimport&nbsp;org.apache.shiro.web.session.mgt.DefaultWebSessionManager;\nimport&nbsp;org.springframework.boot.web.servlet.FilterRegistrationBean;\nimport&nbsp;org.springframework.context.annotation.Bean;\nimport&nbsp;org.springframework.context.annotation.Configuration;\nimport&nbsp;org.springframework.context.annotation.DependsOn;\nimport&nbsp;org.springframework.web.filter.DelegatingFilterProxy;\n\n/**\n&nbsp;*&nbsp;shiro配置类\n&nbsp;*/\n@Configuration\npublic&nbsp;class&nbsp;ShiroConfiguration&nbsp;{\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;FilterRegistrationBean\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;FilterRegistrationBean&nbsp;filterRegistrationBean()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FilterRegistrationBean&nbsp;filterRegistration&nbsp;=&nbsp;new&nbsp;FilterRegistrationBean();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filterRegistration.setFilter(new&nbsp;DelegatingFilterProxy(&quot;shiroFilter&quot;));\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filterRegistration.setEnabled(true);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filterRegistration.addUrlPatterns(&quot;/*&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filterRegistration.setDispatcherTypes(DispatcherType.REQUEST);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;filterRegistration;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@see&nbsp;org.apache.shiro.spring.web.ShiroFilterFactoryBean\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean(name&nbsp;=&nbsp;&quot;shiroFilter&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;ShiroFilterFactoryBean&nbsp;shiroFilter(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ShiroFilterFactoryBean&nbsp;bean&nbsp;=&nbsp;new&nbsp;ShiroFilterFactoryBean();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;bean.setSecurityManager(securityManager());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;bean.setLoginUrl(&quot;/login&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;bean.setUnauthorizedUrl(&quot;/unauthor&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Map&lt;String,&nbsp;Filter&gt;filters&nbsp;=&nbsp;new&nbsp;HashMap&lt;String,&nbsp;Filter&gt;();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filters.put(&quot;perms&quot;,&nbsp;urlPermissionsFilter());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filters.put(&quot;anon&quot;,&nbsp;new&nbsp;AnonymousFilter());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;bean.setFilters(filters);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Map&lt;String,&nbsp;String&gt;&nbsp;chains&nbsp;=&nbsp;new&nbsp;HashMap&lt;String,&nbsp;String&gt;();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;chains.put(&quot;/login&quot;,&nbsp;&quot;anon&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;chains.put(&quot;/admin/**&quot;,&nbsp;&quot;perms&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;chains.put(&quot;/user/**&quot;,&nbsp;&quot;perms&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;chains.put(&quot;/news/**&quot;,&nbsp;&quot;perms&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;chains.put(&quot;/unauthor&quot;,&nbsp;&quot;anon&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;chains.put(&quot;/logout&quot;,&nbsp;&quot;anon&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;chains.put(&quot;/static/**&quot;,&nbsp;&quot;anon&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;bean.setFilterChainDefinitionMap(chains);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;bean;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@see&nbsp;org.apache.shiro.mgt.SecurityManager\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean(name=&quot;securityManager&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;DefaultWebSecurityManager&nbsp;securityManager()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;DefaultWebSecurityManager&nbsp;manager&nbsp;=&nbsp;new&nbsp;DefaultWebSecurityManager();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;manager.setRealm(userRealm());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;manager.setCacheManager(ehCacheManager());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;manager.setSessionManager(defaultWebSessionManager());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;manager;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@see&nbsp;DefaultWebSessionManager\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean(name=&quot;sessionManager&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;DefaultWebSessionManager&nbsp;defaultWebSessionManager()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;DefaultWebSessionManager&nbsp;sessionManager&nbsp;=&nbsp;new&nbsp;DefaultWebSessionManager();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;sessionManager.setCacheManager(ehCacheManager());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;sessionManager.setGlobalSessionTimeout(1800000);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;sessionManager.setDeleteInvalidSessions(true);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;sessionManager.setSessionValidationSchedulerEnabled(true);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;sessionManager.setDeleteInvalidSessions(true);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;sessionManager;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@see&nbsp;UserRealm---&gt;AuthorizingRealm\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean\n&nbsp;&nbsp;&nbsp;&nbsp;@DependsOn(value=&quot;lifecycleBeanPostProcessor&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;UserRealm&nbsp;userRealm()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;UserRealm&nbsp;userRealm&nbsp;=&nbsp;new&nbsp;UserRealm();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;userRealm.setCacheManager(ehCacheManager());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;userRealm.setCredentialsMatcher(hashedCredentialsMatcher());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;userRealm;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;HashedCredentialsMatcher&nbsp;hashedCredentialsMatcher(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;HashedCredentialsMatcher&nbsp;matcher=new&nbsp;HashedCredentialsMatcher();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;matcher.setStoredCredentialsHexEncoded(true);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;matcher.setHashAlgorithmName(&quot;MD5&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;matcher.setHashIterations(1024);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;matcher;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;URLPermissionsFilter&nbsp;urlPermissionsFilter()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;new&nbsp;URLPermissionsFilter();\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;EhCacheManager&nbsp;ehCacheManager()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;EhCacheManager&nbsp;ehCacheManager=new&nbsp;EhCacheManager();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//ehCacheManager.setCacheManagerConfigFile(&quot;classpath:cache/ehcache.xml&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ehCacheManager.setCacheManager(cacheManager());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;ehCacheManager;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean(name&nbsp;=&nbsp;&quot;shiroCacheManager&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;CacheManager&nbsp;cacheManager(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;new&nbsp;CacheManager(getClass().getResource(&quot;cache/ehcache.xml&quot;));\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Bean\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;LifecycleBeanPostProcessor&nbsp;lifecycleBeanPostProcessor()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;new&nbsp;LifecycleBeanPostProcessor();\n&nbsp;&nbsp;&nbsp;&nbsp;}\n}</pre><p><br/></p>', '2018-02-26 10:17:15', '1', '2018-04-26 18:55:03', NULL, '0', '1'),
	(14, 2, 'url拦截配置', '<pre class="brush:java;toolbar:false">package&nbsp;com.ruixin.common.security;\nimport&nbsp;java.io.IOException;\n\nimport&nbsp;javax.servlet.ServletRequest;\nimport&nbsp;javax.servlet.ServletResponse;\nimport&nbsp;javax.servlet.http.HttpServletRequest;\n\nimport&nbsp;com.ruixin.bean.Role;\nimport&nbsp;com.ruixin.bean.User;\nimport&nbsp;com.ruixin.util.UserUtils;\nimport&nbsp;org.apache.commons.lang3.StringUtils;\nimport&nbsp;org.apache.shiro.web.filter.authz.PermissionsAuthorizationFilter;\nimport&nbsp;org.springframework.stereotype.Component;\n\n/**\n&nbsp;*&nbsp;连接权限管理类\n&nbsp;*/\n@Component(&quot;urlPermissionsFilter&quot;)\npublic&nbsp;class&nbsp;URLPermissionsFilter&nbsp;extends&nbsp;PermissionsAuthorizationFilter{\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;boolean&nbsp;isAccessAllowed(ServletRequest&nbsp;request,&nbsp;ServletResponse&nbsp;response,&nbsp;Object&nbsp;mappedValue)&nbsp;throws&nbsp;IOException&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;curUrl&nbsp;=&nbsp;getRequestUrl(request);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(StringUtils.endsWithAny(curUrl,&nbsp;&quot;.js&quot;,&quot;.css&quot;,&quot;.html&quot;,&quot;.jsp&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;||&nbsp;StringUtils.endsWithAny(curUrl,&nbsp;&quot;.jpg&quot;,&quot;.png&quot;,&quot;.gif&quot;,&nbsp;&quot;.jpeg&quot;))&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;User&nbsp;user=UserUtils.getUser();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(user==null||(user!=null&amp;&amp;user.getRoles()==null)){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(Role&nbsp;role:user.getRoles()){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(curUrl.contains(role.getName())){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;获取当前URL+Parameter\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@author&nbsp;lance\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@since&nbsp;&nbsp;2014年12月18日&nbsp;下午3:09:26\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;request&nbsp;&nbsp;拦截请求request\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;返回完整URL\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;String&nbsp;getRequestUrl(ServletRequest&nbsp;request)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;HttpServletRequest&nbsp;req&nbsp;=&nbsp;(HttpServletRequest)request;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;queryString&nbsp;=&nbsp;req.getQueryString();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;queryString&nbsp;=&nbsp;StringUtils.isBlank(queryString)?&quot;&quot;:&nbsp;&quot;?&quot;+queryString;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;req.getRequestURI()+queryString;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n}</pre><p><br/></p>', '2018-02-26 10:25:03', '1', '2018-04-26 18:54:52', NULL, '0', '6'),
	(15, 2, 'shiro配置UserRealm', '<pre class="brush:java;toolbar:false">package&nbsp;com.ruixin.common.security;\n\n\nimport&nbsp;com.ruixin.bean.User;\nimport&nbsp;com.ruixin.service.UserService;\nimport&nbsp;org.apache.shiro.authc.*;\nimport&nbsp;org.apache.shiro.authc.credential.HashedCredentialsMatcher;\nimport&nbsp;org.apache.shiro.authz.AuthorizationInfo;\nimport&nbsp;org.apache.shiro.authz.SimpleAuthorizationInfo;\nimport&nbsp;org.apache.shiro.realm.AuthorizingRealm;\nimport&nbsp;org.apache.shiro.subject.PrincipalCollection;\nimport&nbsp;org.apache.shiro.util.ByteSource;\nimport&nbsp;org.springframework.beans.factory.annotation.Autowired;\nimport&nbsp;org.springframework.stereotype.Component;\n\n/**\n&nbsp;*&nbsp;验证用户登录\n&nbsp;*/\n@Component(&quot;userRealm&quot;)\npublic&nbsp;class&nbsp;UserRealm&nbsp;extends&nbsp;AuthorizingRealm&nbsp;{\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Autowired\n&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;UserService&nbsp;userService;\n\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;UserRealm()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;setName(&quot;UserRealm&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;//权限资源角色\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;protected&nbsp;AuthorizationInfo&nbsp;doGetAuthorizationInfo(PrincipalCollection&nbsp;principals)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;User&nbsp;user&nbsp;=&nbsp;(User)&nbsp;getAvailablePrincipal(principals);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//String&nbsp;username&nbsp;=&nbsp;(String)&nbsp;principals.getPrimaryPrincipal();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SimpleAuthorizationInfo&nbsp;info&nbsp;=&nbsp;new&nbsp;SimpleAuthorizationInfo();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.err.println(&quot;-------------------------------&quot;+user.getUsername());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//add&nbsp;Permission&nbsp;Resources\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//info.setStringPermissions(userService.findPermissions(username));\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//add&nbsp;Roles&nbsp;String[Set&lt;String&gt;&nbsp;roles]\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//info.setRoles(roles);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;info;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;//登录验证\n&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;protected&nbsp;AuthenticationInfo&nbsp;doGetAuthenticationInfo(AuthenticationToken&nbsp;token)&nbsp;throws&nbsp;AuthenticationException&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;UsernamePasswordToken&nbsp;upt&nbsp;=&nbsp;(UsernamePasswordToken)&nbsp;token;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;userName&nbsp;=&nbsp;upt.getUsername();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//查询数据库\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;User&nbsp;user&nbsp;=&nbsp;userService.findByAccount(userName);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(user&nbsp;==&nbsp;null)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;throw&nbsp;new&nbsp;UnknownAccountException();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(user.getStatus().equals(User.STATUS_AUDIT)){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;throw&nbsp;new&nbsp;LockedAccountException();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(user.getStatus().equals(User.STATUS_DELETE)){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;throw&nbsp;new&nbsp;DisabledAccountException();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ByteSource&nbsp;credentialsSalt&nbsp;=&nbsp;ByteSource.Util.bytes(user.getUsername());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SimpleAuthenticationInfo&nbsp;info&nbsp;=&nbsp;new&nbsp;SimpleAuthenticationInfo(user,&nbsp;user.getPassword(),credentialsSalt,&nbsp;getName());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;info;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n}</pre><p><br/></p>', '2018-02-26 10:25:46', '1', '2018-04-28 20:44:56', '1', '0', '5'),
	(16, 2, 'CrudService工具类', '<pre class="brush:java;toolbar:false">package&nbsp;com.ruixin.common.service;\n\nimport&nbsp;java.util.Arrays;\nimport&nbsp;java.util.List;\n\nimport&nbsp;com.github.pagehelper.PageHelper;\nimport&nbsp;com.github.pagehelper.PageInfo;\nimport&nbsp;com.ruixin.common.dao.CrudDao;\nimport&nbsp;com.ruixin.common.entity.DataEntity;\nimport&nbsp;com.ruixin.common.entity.Page;\nimport&nbsp;com.ruixin.common.utils.CacheNames;\nimport&nbsp;org.apache.ibatis.annotations.Param;\nimport&nbsp;org.springframework.beans.factory.annotation.Autowired;\nimport&nbsp;org.springframework.cache.annotation.*;\nimport&nbsp;org.springframework.transaction.annotation.Transactional;\nimport&nbsp;org.springframework.util.CollectionUtils;\n\n/**\n&nbsp;*&nbsp;Service基类\n&nbsp;*/\n@Transactional(readOnly&nbsp;=&nbsp;true)\n@CacheConfig(cacheNames&nbsp;=&nbsp;CacheNames.SYS_CACHE)\npublic&nbsp;class&nbsp;CrudService&lt;D&nbsp;extends&nbsp;CrudDao&lt;T&gt;,&nbsp;T&nbsp;extends&nbsp;DataEntity&lt;T&gt;&gt;{\n&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;持久层对象\n&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;@Autowired\n&nbsp;&nbsp;&nbsp;protected&nbsp;D&nbsp;dao;\n&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;获取单条数据\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;id\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;@Cacheable\n&nbsp;&nbsp;&nbsp;public&nbsp;T&nbsp;get(int&nbsp;id)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;dao.get(id);\n&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;获取单条数据\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;entity\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;@Cacheable\n&nbsp;&nbsp;&nbsp;public&nbsp;T&nbsp;get(T&nbsp;entity)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;dao.get(entity);\n&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;查询列表数据\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;entity\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;@Cacheable\n&nbsp;&nbsp;&nbsp;public&nbsp;List&lt;T&gt;&nbsp;findList(T&nbsp;entity)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;dao.findList(entity);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;@Cacheable\n&nbsp;&nbsp;&nbsp;public&nbsp;Page&lt;T&gt;&nbsp;findPageList(Page&lt;T&gt;&nbsp;page,T&nbsp;entity)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;entity.setPages(page);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;PageHelper.startPage(page.getPage(),page.getLimit());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;List&lt;T&gt;&nbsp;list=dao.findList(entity);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;PageInfo&nbsp;pageInfo&nbsp;=&nbsp;new&nbsp;PageInfo(list);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;page.setData(list);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;page.setPageCount(page.getPageNum());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;page.setCount(pageInfo.getTotal());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;page;\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;查询列表数据总记录数\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;entity\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;@Cacheable(key&nbsp;=&nbsp;&quot;#entity.id+&#39;findListCont&#39;&quot;)\n&nbsp;&nbsp;&nbsp;public&nbsp;long&nbsp;findListCount(T&nbsp;entity)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;dao.findListCount(entity);\n&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;保存数据（插入或更新）\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;entity\n&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;@Transactional(readOnly&nbsp;=&nbsp;false)\n&nbsp;&nbsp;&nbsp;@CachePut\n&nbsp;&nbsp;&nbsp;public&nbsp;void&nbsp;save(T&nbsp;entity)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(entity.getId()==0){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;entity.preInsert();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dao.insert(entity);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;entity.preUpdate();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dao.update(entity);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;删除数据\n&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;entity\n&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;@Transactional(readOnly&nbsp;=&nbsp;false)\n&nbsp;&nbsp;&nbsp;@CacheEvict\n&nbsp;&nbsp;&nbsp;public&nbsp;void&nbsp;delete(T&nbsp;entity)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dao.delete(entity);\n&nbsp;&nbsp;&nbsp;}\n}</pre><p><br/></p>', '2018-02-26 10:27:30', '1', '2019-08-18 16:09:17', '1', '0', '119'),
	(17, 1, '雨声潺潺，像住在溪边。宁愿天天下雨，以为你是因为下雨不来', '<pre class="brush:html;toolbar:false">&lt;p&nbsp;style=&quot;text-align:&nbsp;left;&nbsp;text-indent:&nbsp;2em;&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“雨声潺潺，像住在溪边。宁愿天天下雨，以为你是因为下雨不来。”聪颖早熟、个性独立如张爱玲本不愿选择等待，然而这雨声潺潺分明是一种孤独等待的渲染。&lt;br/&gt;\n&lt;/p&gt;\n&lt;p&nbsp;style=&quot;text-align:&nbsp;left;&nbsp;text-indent:&nbsp;2em;&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;《小团圆》不似张爱玲其他小说的传奇写作，这是更贴近现实的自传体小说，九莉之于邵之雍，正如张爱玲之于胡兰成，乱世的一场爱恋是一段疼痛的成长经历，几乎一生都难以解脱。小团圆之所以不是大团圆，是因为九莉打破了邵之雍梦想的中国传统社会中妻妾成群式的三美团圆。大抵康小姐是可以等待的，辛巧玉自然是好的，九莉也是有过等待的，她的等待似乎比任何人都执着，但她的等待最终是幻灭了，她所能等到的只能是所谓的“大团圆”，但这不是她所要的，于是她便只得决绝于幻灭了。&nbsp;战争的年代，人们生活在硝烟的迷离中，活着已经是一种偶然了，遇见一个人爱上一个人亦是珍贵的偶然了。“愿使岁月静好，现世安稳。”这是胡兰成在婚书上写的话，亦是小说中邵之雍写的，只是当初的张爱玲亦或九莉并不知道“现世安稳”并不是两个人的安稳，邵之雍希望战争结束后实现他三美团圆的美梦，而九莉倒宁愿战争永远不要结束，她不过是要和之雍在一起。于是九莉没能实现邵之雍的美梦，邵之雍当然没有让九莉安稳。\n&lt;/p&gt;\n&lt;p&nbsp;style=&quot;text-align:&nbsp;left;&nbsp;text-indent:&nbsp;2em;&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;过三十岁生日那天，夜里在床上看见洋台上的月光，水泥栏干像倒塌了的石碑横卧在那里，浴在晚唐的蓝色的月光中。一千多年前的月色，但是在她三十年已经太多了，墓碑一样沉重的压在心上。战争和感情给九莉双重的疮痛，墓碑一样得沉重。&lt;br/&gt;见了他，她变得很低很低，低到尘埃里，但她心里是欢喜的，从尘埃里开出花来。这是张爱玲在送给胡兰成的照片的背面写的话，一个高傲的才女对一个崇拜者卑微而极至的爱，小说中九莉“直溜下去跪在他跟前抱着他的腿，脸贴在他腿上。”她崇拜他！爱情里面谁都需要付出代价，小说中九莉是用全生命去爱邵之雍的，这是邵之雍非常清楚的，但是他是没有办法用全生命去爱九莉一个的。&nbsp;“一个人可不可以同时爱两个人呢？”九莉听到邵之雍这样的话后，她感觉到心在往下沉，而邵之雍却是不能明白&nbsp;“她是以她的全生命来爱我的，但是她现在叫我永远不要再写信给她了……”\n&lt;/p&gt;\n&lt;p&nbsp;style=&quot;text-align:&nbsp;left;&nbsp;text-indent:&nbsp;2em;&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你是到底不肯，我想过，我倘使不得不离开你，亦不致寻短见，亦不能再爱别人，我将只是萎谢了。”这是张爱玲在给胡兰成的信上写的，恐怕九莉也不知道，当爱情全部幻灭之后终究还剩下些什么！\n&lt;/p&gt;\n&lt;p&nbsp;style=&quot;text-align:&nbsp;left;&nbsp;text-indent:&nbsp;2em;&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;九莉只是偶然遭遇了爱情，但是既然遭遇了，那就没什么道理了，不管邵之雍已经结过两次婚，不管他有多大，后来哪怕他是汉奸，那也无所谓，最后她知道了“汉奸妻，人人可欺”，她也只是承受罢了，大概人人痛恨他，她也是可以爱他的，“没有目的才是真”，这是她相信并希望的，她所要的不过是爱情！&nbsp;“雨声潺潺，像住在溪边。宁愿天天下雨，以为你是因为下雨不来。”到底有什么值得等待，只因一个女人内心最真挚的爱！&lt;br/&gt;\n&lt;/p&gt;</pre><p><br/></p>', '2018-04-28 21:25:13', '1', '2019-08-18 15:41:51', '19', '0', '20'),
	(18, 2, '文章测试', '<pre class="brush:java;toolbar:false">package&nbsp;com.baidu.ueditor;\n\npublic&nbsp;class&nbsp;Encoder&nbsp;{\n\n&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;String&nbsp;toUnicode&nbsp;(&nbsp;String&nbsp;input&nbsp;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;StringBuilder&nbsp;builder&nbsp;=&nbsp;new&nbsp;StringBuilder();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;char[]&nbsp;chars&nbsp;=&nbsp;input.toCharArray();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(&nbsp;char&nbsp;ch&nbsp;:&nbsp;chars&nbsp;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(&nbsp;ch&nbsp;&lt;&nbsp;256&nbsp;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;builder.append(&nbsp;ch&nbsp;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;else&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;builder.append(&nbsp;&quot;\\\\u&quot;&nbsp;+&nbsp;&nbsp;Integer.toHexString(&nbsp;ch&amp;&nbsp;0xffff&nbsp;)&nbsp;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;builder.toString();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;\n}</pre><p><br/></p>', '2018-05-04 20:53:42', '1', '2019-08-18 15:32:08', '13', '0', '7'),
	(19, 1, '这是一个测试文章', '<pre class="brush:java;toolbar:false">package&nbsp;com.ruixin.controller;\n\nimport&nbsp;com.ruixin.bean.News;\nimport&nbsp;com.ruixin.bean.Type;\nimport&nbsp;com.ruixin.common.entity.Page;\nimport&nbsp;com.ruixin.common.utils.Views;\nimport&nbsp;com.ruixin.service.LinkService;\nimport&nbsp;com.ruixin.service.NewsService;\nimport&nbsp;org.springframework.beans.factory.annotation.Autowired;\nimport&nbsp;org.springframework.stereotype.Controller;\nimport&nbsp;org.springframework.ui.Model;\nimport&nbsp;org.springframework.web.bind.annotation.GetMapping;\nimport&nbsp;org.springframework.web.bind.annotation.PathVariable;\n\n\n/**\n&nbsp;*&nbsp;前台管理类\n&nbsp;*/\n@Controller\npublic&nbsp;class&nbsp;IndexController&nbsp;{\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Autowired\n&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;NewsService&nbsp;newsService;\n\n&nbsp;&nbsp;&nbsp;&nbsp;@Autowired\n&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;LinkService&nbsp;linkService;\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;跳转到首页\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;model\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;page\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@GetMapping({&quot;/&quot;,&quot;/index&quot;})\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;index(Model&nbsp;model,&nbsp;Page&lt;News&gt;&nbsp;page){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;typeList&quot;,newsService.findTypeList());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Page&lt;News&gt;&nbsp;page1=newsService.findPageList(page,new&nbsp;News());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;page&quot;,page1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;links&quot;,linkService.findList(null));\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Views.INDEX;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;跳转到二级页面\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;model\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@GetMapping(&quot;/second/{typeId}&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;second(Model&nbsp;model,@PathVariable(&quot;typeId&quot;)Integer&nbsp;typeId,Page&lt;News&gt;&nbsp;page){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;News&nbsp;news=new&nbsp;News();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;news.setTypeId(typeId);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;typeList&quot;,newsService.findTypeList());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;typeId&quot;,typeId);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;page&quot;,newsService.findPageList(page,news));\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Views.SECOND;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;跳转到三级页面\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;model\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;id\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@return\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@GetMapping(&quot;/third/{id}&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;third(Model&nbsp;model,&nbsp;@PathVariable(&quot;id&quot;)int&nbsp;id){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;typeList&quot;,newsService.findTypeList());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;News&nbsp;news=newsService.get(id);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;news&quot;,news);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;news.setRead(String.valueOf(Integer.parseInt(news.getRead())+1));\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;newsService.save(news);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Views.THIRD;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;关于我\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;@GetMapping(&quot;/about&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;about(Model&nbsp;model){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;model.addAttribute(&quot;typeList&quot;,newsService.findTypeList());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Views.ABOUT;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n}</pre><p><br/></p>', '2018-05-04 21:01:33', '13', '2019-08-18 16:09:14', '13', '0', '3'),
